                {
                    "def": "CREATE OR REPLACE FUNCTION public._rnd_money(lo numeric, hi numeric)\n RETURNS numeric\n LANGUAGE sql\n IMMUTABLE\nAS $function$\r\n  SELECT round( (random() * (hi - lo) + lo)::numeric, 2 );\r\n$function$\n",
                    "name": "_rnd_money"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.app_broker_id()\n RETURNS uuid\n LANGUAGE sql\n STABLE\nAS $function$\r\n  select (select p.broker_id from public.profiles p where p.id = auth.uid());\r\n$function$\n",
                    "name": "app_broker_id"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.assign_pending_to_office_after_3m()\n RETURNS integer\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE\r\n  v_office_broker_id UUID;\r\n  v_updated_count INTEGER;\r\nBEGIN\r\n  -- Find the OFFICE broker by email\r\n  SELECT b.id INTO v_office_broker_id\r\n  FROM public.brokers b\r\n  INNER JOIN public.profiles p ON p.id = b.id\r\n  WHERE LOWER(p.email) = 'contacto@lideresenseguros.com'\r\n  AND b.active = true\r\n  LIMIT 1;\r\n  \r\n  IF v_office_broker_id IS NULL THEN\r\n    RAISE NOTICE 'Office broker not found (contacto@lideresenseguros.com)';\r\n    RETURN 0;\r\n  END IF;\r\n  \r\n  -- Update pending items older than 3 months\r\n  UPDATE public.pending_items\r\n  SET \r\n    assigned_broker_id = v_office_broker_id,\r\n    status = 'auto_office',\r\n    assigned_at = NOW(),\r\n    assignment_notes = 'Auto-assigned to OFFICE after 3 months (90 days)'\r\n  WHERE assigned_broker_id IS NULL\r\n    AND status = 'open'\r\n    AND created_at <= NOW() - INTERVAL '90 days';\r\n  \r\n  GET DIAGNOSTICS v_updated_count = ROW_COUNT;\r\n  \r\n  RAISE NOTICE 'Auto-assigned % pending items to OFFICE', v_updated_count;\r\n  RETURN v_updated_count;\r\nEND;\r\n$function$\n",
                    "name": "assign_pending_to_office_after_3m"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.auto_create_pending_policy()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nBEGIN\r\n  -- Create pending_policy entry for this item\r\n  INSERT INTO public.pending_policy (pending_item_id, policy_number, client_name)\r\n  VALUES (NEW.id, NEW.policy_number, NEW.insured_name)\r\n  ON CONFLICT (pending_item_id, policy_number) DO NOTHING;\r\n  \r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "auto_create_pending_policy"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.batch_update_clients_policies_from_commissions()\n RETURNS TABLE(updated_policies integer, updated_clients integer, errors text[])\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n  v_updated_policies INT := 0;\r\n  v_updated_clients INT := 0;\r\n  v_errors TEXT[] := ARRAY[]::TEXT[];\r\n  v_record RECORD;\r\nBEGIN\r\n  -- Actualizar plizas sin broker asignado\r\n  WITH updates AS (\r\n    UPDATE policies p\r\n    SET broker_id = ci.broker_id,\r\n        updated_at = NOW()\r\n    FROM (\r\n      SELECT DISTINCT ON (policy_number) \r\n        policy_number, \r\n        broker_id\r\n      FROM comm_items\r\n      WHERE broker_id IS NOT NULL\r\n      ORDER BY policy_number, created_at DESC\r\n    ) ci\r\n    WHERE p.policy_number = ci.policy_number\r\n      AND (p.broker_id IS NULL OR p.broker_id != ci.broker_id)\r\n    RETURNING 1\r\n  )\r\n  SELECT COUNT(*) INTO v_updated_policies FROM updates;\r\n\r\n  -- Actualizar clientes sin broker asignado\r\n  WITH updates AS (\r\n    UPDATE clients c\r\n    SET broker_id = p.broker_id,\r\n        updated_at = NOW()\r\n    FROM policies p\r\n    WHERE c.id = p.client_id\r\n      AND p.broker_id IS NOT NULL\r\n      AND (c.broker_id IS NULL OR c.broker_id != p.broker_id)\r\n    RETURNING 1\r\n  )\r\n  SELECT COUNT(*) INTO v_updated_clients FROM updates;\r\n\r\n  RETURN QUERY SELECT v_updated_policies, v_updated_clients, v_errors;\r\nEND;\r\n$function$\n",
                    "name": "batch_update_clients_policies_from_commissions"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.cleanup_processed_temp_imports()\n RETURNS integer\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE\r\n  deleted_count INTEGER;\r\nBEGIN\r\n  DELETE FROM public.temp_client_imports\r\n  WHERE import_status = 'processed'\r\n  AND processed_at < NOW() - INTERVAL '7 days';\r\n  \r\n  GET DIAGNOSTICS deleted_count = ROW_COUNT;\r\n  RETURN deleted_count;\r\nEND;\r\n$function$\n",
                    "name": "cleanup_processed_temp_imports"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.commissions_monthly_summary(p_year integer, p_broker uuid DEFAULT NULL::uuid)\n RETURNS TABLE(month integer, broker_id uuid, total_amount numeric)\n LANGUAGE sql\n STABLE\nAS $function$\r\n  select\r\n    extract(month from f.start_date)::int as month,\r\n    c.broker_id,\r\n    coalesce(sum(cd.amount), 0) as total_amount\r\n  from public.commission_details cd\r\n  join public.commissions c on c.id = cd.commission_id\r\n  join public.fortnights f on f.id = c.fortnight_id\r\n  where extract(year from f.start_date) = p_year\r\n    and (p_broker is null or c.broker_id = p_broker)\r\n  group by 1,2\r\n  order by 1,2;\r\n$function$\n",
                    "name": "commissions_monthly_summary"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.current_broker_id()\n RETURNS uuid\n LANGUAGE sql\n STABLE\nAS $function$\r\n  select nullif(auth.jwt()->'app_metadata'->>'broker_id','')::uuid;\r\n$function$\n",
                    "name": "current_broker_id"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.delete_processed_temp_import()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nBEGIN\r\n  -- Solo eliminar si fue procesado exitosamente\r\n  IF NEW.import_status = 'processed' THEN\r\n    -- Usar DELETE para remover el registro\r\n    DELETE FROM public.temp_client_imports\r\n    WHERE id = NEW.id;\r\n    \r\n    -- Retornar NULL porque el registro ya fue eliminado\r\n    RETURN NULL;\r\n  END IF;\r\n  \r\n  -- Si hay error, mantener el registro para revisin\r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "delete_processed_temp_import"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.ensure_auth_user_from_broker(p_broker_id uuid)\n RETURNS uuid\n LANGUAGE plpgsql\n SET search_path TO 'public', 'pg_temp'\nAS $function$\r\ndeclare\r\n  s record;\r\n  v_user_id uuid;\r\n  v_pwd  text;\r\n  v_hash text;\r\nbegin\r\n  -- 1) Lee fuente normalizada\r\n  select * into s\r\n  from public.brokers_sync_source\r\n  where broker_id = p_broker_id;\r\n\r\n  if s is null or s.email is null then\r\n    raise exception 'Broker % no encontrado o sin email', p_broker_id;\r\n  end if;\r\n\r\n  -- 2) Ya existe user por email? (case-insensitive)\r\n  select u.id into v_user_id\r\n  from auth.users u\r\n  where lower(u.email) = lower(s.email)\r\n  limit 1;\r\n\r\n  -- 3) Hash temporal slo si vamos a crear (evita pisar claves existentes)\r\n  v_pwd  := coalesce(s.plain_password, 'Temporal123!');\r\n  v_hash := public.hash_pwd(v_pwd);  -- usa extensions.gen_salt/crypt adentro\r\n\r\n  if v_user_id is null then\r\n    -- Crear usuario nuevo\r\n    insert into auth.users (id, email, encrypted_password, instance_id, role, email_confirmed_at)\r\n    values (\r\n      coalesce(s.auth_id, gen_random_uuid()),\r\n      s.email,\r\n      v_hash,\r\n      '00000000-0000-0000-0000-000000000000',\r\n      'authenticated',\r\n      now()\r\n    )\r\n    returning id into v_user_id;\r\n  else\r\n    -- Actualizar datos mnimos del existente (NO tocamos password si ya tiene)\r\n    update auth.users\r\n       set email = s.email\r\n     where id = v_user_id;\r\n  end if;\r\n\r\n  -- 4) Guarda relacin en brokers\r\n  update public.brokers\r\n     set auth_id = v_user_id\r\n   where id = p_broker_id\r\n     and (auth_id is null or auth_id <> v_user_id);\r\n\r\n  return v_user_id;\r\nend\r\n$function$\n",
                    "name": "ensure_auth_user_from_broker"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.get_pending_items_grouped()\n RETURNS TABLE(policy_number text, client_name text, total_items bigint, total_commission numeric, oldest_date timestamp with time zone, status text, items jsonb)\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nBEGIN\r\n  RETURN QUERY\r\n  SELECT \r\n    pp.policy_number,\r\n    pp.client_name,\r\n    COUNT(pi.id) AS total_items,\r\n    SUM(pi.commission_raw) AS total_commission,\r\n    MIN(pi.created_at) AS oldest_date,\r\n    pi.status,\r\n    jsonb_agg(\r\n      jsonb_build_object(\r\n        'id', pi.id,\r\n        'insured_name', pi.insured_name,\r\n        'commission_raw', pi.commission_raw,\r\n        'fortnight_id', pi.fortnight_id,\r\n        'created_at', pi.created_at\r\n      )\r\n    ) AS items\r\n  FROM public.pending_policy pp\r\n  INNER JOIN public.pending_items pi ON pi.id = pp.pending_item_id\r\n  GROUP BY pp.policy_number, pp.client_name, pi.status\r\n  ORDER BY oldest_date ASC;\r\nEND;\r\n$function$\n",
                    "name": "get_pending_items_grouped"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.handle_new_user_full()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE\r\n  v_role public.role_enum := 'BROKER'; -- por defecto todos entran como BROKER; luego Master puede ascenderlos\r\n  v_name text;\r\n  v_pct numeric;\r\nBEGIN\r\n  -- Nombre: de metadata si viene, si no, parte local del email\r\n  v_name := COALESCE(\r\n    NEW.raw_user_meta_data->>'full_name',\r\n    split_part(NEW.email, '@', 1)\r\n  );\r\n\r\n  -- Porcentaje por defecto\r\n  IF lower(NEW.email) = 'contacto@lideresenseguros.com' THEN\r\n    v_pct := 1.00;\r\n    v_role := 'MASTER'; -- (opcional) si esa cuenta es la oficina y debe entrar como MASTER\r\n  ELSE\r\n    v_pct := 0.80;\r\n  END IF;\r\n\r\n  -- Crea profile si no existe\r\n  INSERT INTO public.profiles (id, full_name, role, email, created_at)\r\n  VALUES (NEW.id, v_name, v_role, NEW.email, NOW())\r\n  ON CONFLICT (id) DO NOTHING;\r\n\r\n  -- Crea broker enlazado si no existe\r\n  INSERT INTO public.brokers (id, name, email, percent_default, active, created_at, p_id)\r\n  VALUES (NEW.id, v_name, NEW.email, v_pct, TRUE, NOW(), NEW.id)\r\n  ON CONFLICT (id) DO NOTHING;\r\n\r\n  -- Enlaza profile  broker\r\n  UPDATE public.profiles\r\n  SET broker_id = NEW.id\r\n  WHERE id = NEW.id\r\n    AND broker_id IS NULL;\r\n\r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "handle_new_user_full"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.is_master()\n RETURNS boolean\n LANGUAGE sql\n STABLE SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\r\n  SELECT EXISTS (\r\n    SELECT 1\r\n    FROM public.profiles p\r\n    WHERE p.id = auth.uid()\r\n      AND p.role = 'master'::public.role_enum\r\n  );\r\n$function$\n",
                    "name": "is_master"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.is_self(broker uuid)\n RETURNS boolean\n LANGUAGE sql\n STABLE\nAS $function$\r\n  select exists (\r\n    select 1\r\n    from public.brokers b\r\n    where b.id = broker\r\n      and b.p_id = auth.uid()\r\n  );\r\n$function$\n",
                    "name": "is_self"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.metrics_broker(p_broker uuid, p_fortnight uuid)\n RETURNS TABLE(collected_amount numeric, morosity_over_60 numeric, pending_identify integer)\n LANGUAGE sql\n STABLE\nAS $function$\r\n  select\r\n    coalesce(sum(c.total_amount),0),\r\n    coalesce(sum(case when f.end_date < now() - interval '60 days' then c.total_amount else 0 end),0),\r\n    0\r\n  from public.commissions c\r\n  join public.fortnights f on f.id = c.fortnight_id\r\n  where c.broker_id = p_broker\r\n    and c.fortnight_id = p_fortnight;\r\n$function$\n",
                    "name": "metrics_broker"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.process_temp_client_import()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE\r\n  v_client_id UUID;\r\n  v_policy_id UUID;\r\n  v_broker_id UUID;\r\n  v_insurer_id UUID;\r\n  v_broker_default_percent NUMERIC(5,2);\r\nBEGIN\r\n  -- Solo procesar si tiene national_id (requisito para oficial)\r\n  IF NEW.national_id IS NULL OR NEW.import_status = 'processed' THEN\r\n    RETURN NEW;\r\n  END IF;\r\n\r\n  -- Resolver broker_id desde email\r\n  SELECT b.id, b.default_percent INTO v_broker_id, v_broker_default_percent\r\n  FROM public.brokers b\r\n  INNER JOIN public.profiles p ON p.id = b.id\r\n  WHERE p.email = NEW.broker_email;\r\n\r\n  IF v_broker_id IS NULL THEN\r\n    NEW.import_status = 'error';\r\n    NEW.error_message = 'Broker no encontrado con email: ' || NEW.broker_email;\r\n    NEW.processed_at = NOW();\r\n    RETURN NEW;\r\n  END IF;\r\n\r\n  -- Resolver insurer_id desde nombre\r\n  SELECT id INTO v_insurer_id\r\n  FROM public.insurers\r\n  WHERE LOWER(name) = LOWER(TRIM(NEW.insurer_name))\r\n  AND active = true;\r\n\r\n  IF v_insurer_id IS NULL THEN\r\n    NEW.import_status = 'error';\r\n    NEW.error_message = 'Aseguradora no encontrada o inactiva: ' || NEW.insurer_name;\r\n    NEW.processed_at = NOW();\r\n    RETURN NEW;\r\n  END IF;\r\n\r\n  -- Validar que policy_number no exista ya\r\n  SELECT id INTO v_policy_id\r\n  FROM public.policies\r\n  WHERE policy_number = NEW.policy_number;\r\n\r\n  IF v_policy_id IS NOT NULL THEN\r\n    NEW.import_status = 'error';\r\n    NEW.error_message = 'Nmero de pliza ya existe: ' || NEW.policy_number;\r\n    NEW.processed_at = NOW();\r\n    RETURN NEW;\r\n  END IF;\r\n\r\n  -- PASO 1: Buscar o crear cliente\r\n  SELECT id INTO v_client_id\r\n  FROM public.clients\r\n  WHERE national_id = NEW.national_id\r\n  AND broker_id = v_broker_id;\r\n\r\n  IF v_client_id IS NULL THEN\r\n    -- Crear nuevo cliente\r\n    INSERT INTO public.clients (\r\n      name, national_id, email, phone, broker_id, active\r\n    ) VALUES (\r\n      NEW.client_name,\r\n      NEW.national_id,\r\n      NEW.email,\r\n      NEW.phone,\r\n      v_broker_id,\r\n      true\r\n    ) RETURNING id INTO v_client_id;\r\n  ELSE\r\n    -- Cliente existe, actualizar datos si estn presentes\r\n    UPDATE public.clients SET\r\n      name = COALESCE(NEW.client_name, name),\r\n      email = COALESCE(NEW.email, email),\r\n      phone = COALESCE(NEW.phone, phone)\r\n    WHERE id = v_client_id;\r\n  END IF;\r\n\r\n  -- PASO 2: Crear pliza\r\n  INSERT INTO public.policies (\r\n    client_id,\r\n    broker_id,\r\n    insurer_id,\r\n    policy_number,\r\n    ramo,\r\n    start_date,\r\n    renewal_date,\r\n    status,\r\n    percent_override\r\n  ) VALUES (\r\n    v_client_id,\r\n    v_broker_id,\r\n    v_insurer_id,\r\n    NEW.policy_number,\r\n    NEW.ramo,\r\n    NEW.start_date,\r\n    NEW.renewal_date,\r\n    COALESCE(NEW.status, 'active')::policy_status_enum,\r\n    NEW.percent_override -- NULL usa el default del broker\r\n  ) RETURNING id INTO v_policy_id;\r\n\r\n  -- Marcar como procesado exitosamente\r\n  NEW.import_status = 'processed';\r\n  NEW.error_message = NULL;\r\n  NEW.processed_at = NOW();\r\n\r\n  RETURN NEW;\r\n\r\nEXCEPTION WHEN OTHERS THEN\r\n  NEW.import_status = 'error';\r\n  NEW.error_message = SQLERRM;\r\n  NEW.processed_at = NOW();\r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "process_temp_client_import"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.profile_sync_from_auth(p_user_id uuid)\n RETURNS void\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\r\ndeclare\r\n  v_email text;\r\n  v_role_txt text;\r\n  v_broker uuid;\r\nbegin\r\n  select\r\n    u.email,\r\n    u.raw_app_meta_data->>'role',\r\n    nullif(u.raw_app_meta_data->>'broker_id','')::uuid\r\n  into v_email, v_role_txt, v_broker\r\n  from auth.users u\r\n  where u.id = p_user_id;\r\n\r\n  if v_role_txt is null or v_role_txt = '' then\r\n    v_role_txt := 'broker';\r\n  end if;\r\n\r\n  insert into public.profiles (id, email, role, broker_id)\r\n  values (p_user_id, v_email, v_role_txt::role_enum, v_broker)\r\n  on conflict (id) do update\r\n    set email = excluded.email,\r\n        role = excluded.role,\r\n        broker_id = excluded.broker_id;\r\nend$function$\n",
                    "name": "profile_sync_from_auth"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.set_user_role(p_email text, p_role role_enum, p_broker_id uuid DEFAULT NULL::uuid)\n RETURNS void\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\r\ndeclare\r\n  v_uid uuid;\r\n  v_meta jsonb;\r\nbegin\r\n  select id into v_uid\r\n  from auth.users\r\n  where lower(email) = lower(p_email)\r\n  limit 1;\r\n\r\n  if v_uid is null then\r\n    raise exception 'No existe usuario con email %', p_email;\r\n  end if;\r\n\r\n  select coalesce(raw_app_meta_data, '{}'::jsonb) into v_meta\r\n  from auth.users where id = v_uid;\r\n\r\n  v_meta := v_meta || jsonb_build_object('role', p_role::text);\r\n  if p_broker_id is not null then\r\n    v_meta := v_meta || jsonb_build_object('broker_id', p_broker_id::text);\r\n  end if;\r\n\r\n  update auth.users\r\n  set raw_app_meta_data = v_meta\r\n  where id = v_uid;\r\n\r\n  -- asegura profiles con email/rol/broker al da\r\n  perform public.profile_sync_from_auth(v_uid);\r\nend$function$\n",
                    "name": "set_user_role"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.set_user_role(p_email text, p_role text, p_broker_id uuid DEFAULT NULL::uuid)\n RETURNS void\n LANGUAGE plpgsql\nAS $function$\r\ndeclare\r\n  v_meta jsonb;\r\nbegin\r\n  update auth.users\r\n  set raw_app_meta_data = coalesce(raw_app_meta_data, '{}'::jsonb)\r\n         || jsonb_build_object('role', p_role)\r\n         || case when p_broker_id is null then '{}'::jsonb\r\n                 else jsonb_build_object('broker_id', p_broker_id::text) end\r\n  where email = p_email;\r\nend;\r\n$function$\n",
                    "name": "set_user_role"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.trg_brokers_sync()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nbegin\r\n  perform public.ensure_auth_user_from_broker(coalesce(new.id, old.id));\r\n  return new;\r\nend $function$\n",
                    "name": "trg_brokers_sync"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.trg_brokers_sync_iud()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nbegin\r\n  if (tg_op in ('INSERT','UPDATE')) then\r\n    perform public.ensure_auth_user_from_broker(new.id);\r\n  elsif (tg_op = 'DELETE') then\r\n    -- Si eliminas el broker, opcionalmente elimina user/profile\r\n    -- delete from auth.users where id = old.auth_id;\r\n    -- delete from public.profiles where id = old.profile_id;\r\n    null;\r\n  end if;\r\n  return null;\r\nend\r\n$function$\n",
                    "name": "trg_brokers_sync_iud"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.update_can_be_paid()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  -- Actualizar el estado can_be_paid del pago\r\n  UPDATE pending_payments\r\n  SET \r\n    can_be_paid = NOT EXISTS (\r\n      SELECT 1 FROM payment_references\r\n      WHERE payment_id = NEW.payment_id\r\n      AND exists_in_bank = false\r\n    ),\r\n    total_received = (\r\n      SELECT COALESCE(SUM(amount), 0)\r\n      FROM payment_references\r\n      WHERE payment_id = NEW.payment_id\r\n    )\r\n  WHERE id = NEW.payment_id;\r\n  \r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "update_can_be_paid"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.update_clients_policies_from_commissions()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n  v_policy_id UUID;\r\n  v_client_id UUID;\r\n  v_broker_id UUID;\r\n  v_policy_number TEXT;\r\nBEGIN\r\n  -- Solo procesar si el comm_item tiene broker_id\r\n  IF NEW.broker_id IS NULL THEN\r\n    RETURN NEW;\r\n  END IF;\r\n\r\n  v_broker_id := NEW.broker_id;\r\n  v_policy_number := NEW.policy_number;\r\n\r\n  -- Buscar la pliza por policy_number\r\n  SELECT id, client_id INTO v_policy_id, v_client_id\r\n  FROM policies\r\n  WHERE policy_number = v_policy_number\r\n  LIMIT 1;\r\n\r\n  -- Si encontramos la pliza\r\n  IF v_policy_id IS NOT NULL THEN\r\n    \r\n    -- 1. Actualizar broker_id en policies si no tiene uno asignado\r\n    UPDATE policies\r\n    SET broker_id = v_broker_id,\r\n        updated_at = NOW()\r\n    WHERE id = v_policy_id\r\n      AND (broker_id IS NULL OR broker_id != v_broker_id);\r\n\r\n    -- 2. Actualizar broker_id en clients si no tiene uno asignado\r\n    IF v_client_id IS NOT NULL THEN\r\n      UPDATE clients\r\n      SET broker_id = v_broker_id,\r\n          updated_at = NOW()\r\n      WHERE id = v_client_id\r\n        AND (broker_id IS NULL OR broker_id != v_broker_id);\r\n    END IF;\r\n\r\n  END IF;\r\n\r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "update_clients_policies_from_commissions"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.update_updated_at_column()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  NEW.updated_at = NOW();\r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "update_updated_at_column"
                },
                {
                    "def": "CREATE OR REPLACE FUNCTION public.validate_payment_references()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  -- Verificar si la referencia existe en bank_transfers\r\n  NEW.exists_in_bank := EXISTS (\r\n    SELECT 1 FROM bank_transfers \r\n    WHERE reference_number = NEW.reference_number\r\n  );\r\n  \r\n  RETURN NEW;\r\nEND;\r\n$function$\n",
                    "name": "validate_payment_references"
                }
